sources.main.scss:
// & - 상위 선택자 참조
// : 중첩된 속성 - 네임스페이스 생성ㅇ
// $ : 변수
// @ : mixin
// @for $i from 1 through 32 : 반복문
// @function ratio($size, $ratio){ @return $size * $ratio} : 함수
$url: "https://raw.githubuserContent.com/ParkYoungWoong/overwatch-hero-selector-vanilla/master/images";
body {
  height: 100vh;
  background: {
    image: url("#{$url}/bg.jpg");
    size: cover;
    repeat: no-repeat;
    attachment: fixed;
  }
  .container {
    padding: 50px 0;
    @import "./heroes.scss";
    @import "./logo.scss";
  }
}

sources.heroes.scss:
@mixin vertical-center {
  display: flex;
  justify-content: center;
  align-items: center;
}
.heroes {
  @include vertical-center;
  flex-wrap: wrap;
  max-width: 700px;
  margin: auto;
  padding: 40px 20px;
  @import "./hero.scss";
}

sources.hero.scss:
.hero {
  border: {
    width: 3px;
    style: solid;
    color: #ffffff;
    radius: 10px;
  }
  width: 80px;
  height: 84px;
  margin: 4px;
  background-color: #555;
  transform: skewX(-14deg);
  box-sizing: border-box;
  transition: transform 0.1s, background-color 0.6s;
  overflow: hidden;
  &:hover {
    background-color: #ff9c00;
    transform: sce(1.3) skewX(-14deg);
    z-index: 1;
  }
  .image {
    width: 140%;
    height: 100%;
    background: {
      position: center;
      size: 90px;
      repeat: no-repeat;
    }
    transform: skew(14deg) translateX(-16px);
  }
  @mixin image($idx: 1, $type: "png") {
    background-image: url("#{$url}/hero#{$idx}.#{$type}");
  }
  @for $i from 1 through 32 {
    &:nth-child(#{$i}) .image {
      @include image($idx: $i, $type: "png");
    }
  }

  $list: orange, royalblue, yellow;

  $map: (
    o: orange,
    r: royalblue,
    y: yellow,
  );
  @each $c in $list {
    .box {
      color: $c;
    }
  }
  @each $k, $v in $map {
    .box {
      color: $v;
    }
  }
}

